function initEdit(){var inst=tinyMCE.getInstanceById(tinyMCE.getWindowArg('editor_id'));var focusElm=inst.getFocusElement();var nodeName=focusElm.nodeName.toLowerCase();if(((nodeName=="input")&&((focusElm.type=='text')||(focusElm.type=='password')||(focusElm.type=='file')))||(nodeName=="textarea")){editing=focusElm;var singleLine=(nodeName!="textarea");if(singleLine){selectOption('cb_type',focusElm.type.toLowerCase());}else{selectOption('cb_type','multi');}classes=focusElm.className.toLowerCase().split(' ');document.getElementById('chk_valid_mandatory').selected=false;for(var i=0;i<classes.length;i++){switch(classes[i]){case'mandatory':document.getElementById('chk_valid_mandatory').checked=true;break;case'email':selectOption('cb_validation','email');break;case'number':selectOption('cb_validation','number');break;default:selectOption('cb_class',classes[i]);}}document.getElementById('ed_name').value=focusElm.name.replace(/^ed_/,'');document.getElementById('ed_desc').value=focusElm.title;if(singleLine){document.getElementById('ed_value').value=focusElm.value;}else{document.getElementById('ed_value').value=encodeAttribute(focusElm.innerHTML);}}else{editing=null;}}function insertEdit(){var inst=tinyMCE.getInstanceById(tinyMCE.getWindowArg('editor_id'));var className=getSelectValue('cb_class');var typeName=getSelectValue('cb_type');var cName=document.getElementById('ed_name').value;var cDesc=document.getElementById('ed_desc').value;var validation=getSelectValue('cb_validation');var cValue=document.getElementById('ed_value').value;var classes=Array();var singleLine=(typeName!='multi');if(cName==''){alert(tinyMCE.entityDecode(tinyMCE.getLang('lang_amadeo_form_err_name_mandatory')));return false;}else{cName='ed_'+cName;}if(!editing&&inst.contentWindow.document.getElementById(cName)){alert(tinyMCE.entityDecode(tinyMCE.getLang('lang_amadeo_form_err_name_unique')));return false;}if(validation!='text'){classes.push(validation);}if(className!='none'){classes.push(className);}if(document.getElementById('chk_valid_mandatory').checked){classes.push('mandatory');}inst.execCommand('mceBeginUndoLevel');if(editing&&(((editing.nodeName.toLowerCase()=='textarea')&&singleLine)||((editing.nodeName.toLowerCase()=='input')&&!singleLine))){editing.innerHTML='';inst.execCommand('mceReplaceContent',false,'');editing=null;}if(editing!=null){editing.id=cName;editing.name=cName;editing.title=cDesc;editing.className=classes.join(' ');if(singleLine){switch(typeName){case'password':editing.type='password';break;case'file':editing.type='file';break;default:editing.type='text';break;}}if(singleLine){if(typeName!='file'){editing.setAttribute('value',cValue);}}else{editing.innerHTML=cValue;}}else{if(singleLine){html='<input';html+=makeAttrib('type',typeName);}else{html='<textarea';}html+=makeAttrib('id',cName);html+=makeAttrib('name',cName);html+=makeAttrib('title',cDesc);html+=makeAttrib('class',classes.join(' '));if(singleLine){if(typeName!='file'){html+=makeAttrib('value',cValue);}html+='/>';}else{html+='>'+cValue+'</textarea>';}inst.execCommand('mceInsertContent',false,html);}inst.execCommand("mceEndUndoLevel");tinyMCEPopup.close();}