function validateName(control){control.value=control.value.toLowerCase().replace(/[^a-z_0-9-]*/g,'').replace(/^[0-9]*/,'');}function validateNumber(control){control.value=control.value.toLowerCase().replace(/[^0-9-]*/g,'');}function getSelectValue(id){if(elm=document.getElementById(id)){if(elm.selectedIndex>=0){return elm.options[elm.selectedIndex].value;}else{return'';}}else{return'';}}function makeAttrib(attrib,value){return' '+attrib+'="'+encodeAttribute(value)+'"';}function encodeAttribute(value){if(value==""){return"";}value=value.replace(/&/g,'&amp;');value=value.replace(/\"/g,'&quot;');value=value.replace(/</g,'&lt;');return value.replace(/>/g,'&gt;');}function selectOption(selectId,optionValue){if(elm=document.getElementById(selectId)){for(var i=0;i<elm.options.length;i++){elm.options[i].selected=(elm.options[i].value==optionValue);}return true;}else{return false;}}function addToSelect(selectId,optionValue,optionText){var selectElm=document.getElementById(selectId);optionArray=selectElm.options;for(var i=0;i<optionArray.length;i++){if(optionArray[i].value==optionValue){return false;}}var option=document.createElement('option');option.text=optionText;option.value=optionValue;try{selectElm.add(option,null);}catch(ex){selectElm.add(option);}return true;}function clearSelect(selectId){selectElm=document.getElementById(selectId);while(selectElm.options.length>0){selectElm.remove(0);}}